mapper.update:
UPDATE user
username = #{username}, email = #{email}, password_hash = #{passwordHash}, avatar_url = #{avatarUrl}, phone = #{phone}, address = #{address}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT EXISTS (
SELECT 1 FROM user WHERE username = #{username}
)

SELECT EXISTS (
SELECT 1 FROM user WHERE email = #{email}
)
